const btnPrev=document.getElementById("prev"),btnNext=document.getElementById("next"),addCircle=document.getElementById("add"),removeCircle=document.getElementById("remove");var progress=document.getElementById("progress"),progressCtn=document.getElementsByClassName("progress-container"),circles=document.querySelectorAll(".circle"),currentActive=1;function update(){1===currentActive?btnPrev.disabled=!0:currentActive===circles.length?btnNext.disabled=!0:(btnPrev.disabled=!1,btnNext.disabled=!1),circles.forEach((e,t)=>{t<currentActive?e.classList.add("active"):e.classList.remove("active")});const e=document.querySelectorAll(".active");progress.style.width=(e.length-1)/(circles.length-1)*100+"%"}function updateVariables(){progress=document.getElementById("progress"),progressCtn=document.getElementsByClassName("progress-container"),circles=document.querySelectorAll(".circle")}function reset(){currentActive=1,btnPrev.disabled=!0,btnNext.disabled=!1,progress.style.width="0%",updateVariables(),circles.forEach((e,t)=>{0===t?e.classList.add("active"):e.classList.remove("active")})}btnNext.addEventListener("click",()=>{++currentActive>circles.length&&(currentActive=circles.length),update()}),btnPrev.addEventListener("click",()=>{--currentActive<1&&(currentActive=1),update()}),addCircle.addEventListener("click",()=>{if(circles.length>=10)alert("can add max 10 steps.");else{var e=document.createElement("div");e.className="circle",e.innerText=circles.length+1,progressCtn[0].appendChild(e),reset()}}),removeCircle.addEventListener("click",()=>{circles.length>3?circles[circles.length-1].remove():alert("min 3 steps needed."),reset()});